import React, { useState, useEffect } from 'react';

const Patient = () => {

    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(true); 
    const [error, setError] = useState(null); 
    const [pageSize, setPageSize] = useState(4);

    useEffect(() => {
        fetch(`https://localhost:7169/api/Appointment/RecentPatient/${pageSize}`)
        .then(response => {
            if (!response.ok) { 
            throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            setItems(data);
            setLoading(false); 
        })
        .catch(error => {
            console.error('Error fetching data:', error);
            setError(error); 
            setLoading(false);
        });
    }, [pageSize]);

    if (loading) {
        return <div>Loading...</div>; 
    }

    if (error) {
        return <div>Error: {error.message}</div>; 
    }

    return(
        <div>
            <div className="flex items-center justify-center">
                <div className="w-[1516px] mt-28">
                    <div className= "top-20 mb-16">
                        <text className="text-3xl font-semibold">Appoitment Request</text>
                        <button 
                        onClick={() => setPageSize(0)}
                        className={`${pageSize===0 ? 'hidden' : 'visible'} pt-2 pb-2 pr-4 pl-4 ml-[252px]`}>
                            <span className="flex items-center text-cyan-500 font-medium text-lg">
                                View All
                                <img 
                                    className="w-12 h-12 ml-3" 
                                    src="https://img.icons8.com/ios-glyphs/30/22C3E6/more-than.png" 
                                    alt="more than icon" 
                                    aria-hidden="true" 
                                />
                            </span>
                        </button>
                        <button 
                        onClick = {()=> setPageSize(7)}
                        className={`${pageSize===7 ? 'hidden' : 'visible'} absolute pt-2 pb-2 pr-4 pl-4 mt-6 ml-[256px]`}
                        >
                            <span className="flex items-center text-red-600 font-medium text-lg">
                                <img 
                                    className="w-20 h-20 ml-2" 
                                    src="https://img.icons8.com/sf-regular-filled/100/FA5252/x.png" 
                                    alt="x"
                                />
                                Cancel
                            </span>
                        </button>
                    </div>
                    <div className="bg-[#dfe3e6] h-[72px] rounded-xl relative pt-20">
                        <text className="text-gray-700 text-3xl font-medium absolute ml-28">Patient Name</text>
                    </div>
                    <div>
                        {items.map((item, index) => (
                            <div className="mt-10 h-[94px] bg-white text-gray-700 flex pt-20 rounded-xl relative">
                                <div className="ml-28 w-[54px] h-[54px] overflow-hidden flex-none" key={index}>
                                    <img className="w-full h-full object-cover rounded-full" src={item.avatar}></img>
                                </div>
                                <text className="ml-12 text-3xl mt-8">{item.fullName}</text>
                                <text className="absolute ml-[100px] mt-8 text-xl">{item.patientId}</text>
                            </div>
                        ))}
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Patient;
