import React from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom'; // Dùng Routes thay vì Switch
import HomePage from '../pages/HomePage';
import AppointmentRequest from '../component/AppointmentRequest';
import PatientChart from '../component/PatientChart';
import { CacheProvider } from '@emotion/react';
import createCache from '@emotion/cache';
import { SnackbarProvider } from 'notistack';
import { Provider } from 'react-redux';
import store from './store';


const cache = createCache({ key: 'css' });

const App = () => {
  return (
    <Provider store={store}> {/* Redux store */}
      <CacheProvider value={cache}> {/* Css cần tiềm hiểu về CSP */}
        <SnackbarProvider maxSnack={5}> {/* Hiện thông báo đối đa 5 */}
          <Router>
            <Routes>
              <Route path="/" element={<HomePage />} />
              <Route path="/appointmentRequest" element={<AppointmentRequest />} />
              <Route path="/patientChart" element={<PatientChart />} />
            </Routes>
          </Router>
        </SnackbarProvider>
      </CacheProvider>
    </Provider>
  );
};

export default App;
