import React from 'react';
import { useSnackbar } from 'notistack';
/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';
import AppointmentRequest from '../component/AppointmentRequest';
import PatientChart from '../component/PatientChart';
import GenderChart from '../component/GenderChart';

const HomePage = () => {
    const style = css`
    background-color: lightblue;
    padding: 20px;
    border-radius: 5px;
    font-size: 18px;
    text-align: center;
  `;

  const style1 = css`
    background-color: lightblue;
    padding: 20px;
    border-radius: 5px;
    font-size: 18px;
    text-align: center;
  `;

  const { enqueueSnackbar } = useSnackbar();

  const showSuccessMessage = () => {
    enqueueSnackbar('Appointment Request Confirmed', { variant: 'success' });
  };

  const showErrorMessage = () => {
    enqueueSnackbar('Appointment Request Declined!', { variant: 'error' });
  };

  return (
    <div className="bg-white">
      <h1 className="pt-4">Home Page</h1>
      <p css={style}>Welcome to our website! Here you'll find all the latest information and products.</p>
      <h1>Trang chủ</h1>
      <button onClick={showSuccessMessage}>Hiển thị thông báo thành công</button>
      <button onClick={showErrorMessage}>Hiển thị thông báo lỗi</button>
    </div>
  );
};


export default HomePage;
