import React, { useState, useEffect } from 'react';
import { useSnackbar } from 'notistack';

const AppointmentRequest = () => {

    const { enqueueSnackbar } = useSnackbar();
    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(true); 
    const [error, setError] = useState(null); 
    const [status, setStatus] = useState([]);

    const handleStatusChange = async (title, appointmentId) => {
        if (title === 'Confirmed') {
            <div className="bg-blue-50">
                <Dialog  onClose={handleClose} aria-labelledby="confirm-dialog-title">
                    <DialogTitle id="confirm-dialog-title">Confirm Action</DialogTitle>
                    <DialogContent>
                    <DialogContentText>
                        Are you sure you want to change the status of this appointment?
                    </DialogContentText>
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={handleClose} color="secondary">
                        Cancel
                    </Button>
                    <Button onClick={handleConfirm} color="primary" autoFocus>
                        Confirm
                    </Button>
                    </DialogActions>
                </Dialog>
            </div>
        }
        else {
            <div className="bg-red-50">
                <Dialog  onClose={handleClose} aria-labelledby="confirm-dialog-title">
                    <DialogTitle id="confirm-dialog-title">Confirm Action</DialogTitle>
                    <DialogContent>
                    <DialogContentText>
                        Are you sure you want to change the status of this appointment?
                    </DialogContentText>
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={handleClose} color="secondary">
                        Cancel
                    </Button>
                    <Button onClick={handleConfirm} color="primary" autoFocus>
                        Confirm
                    </Button>
                    </DialogActions>
                </Dialog>
            </div>
        }
        //const isConfirmed = window.confirm("Are you sure you want to change the status?");
        
        if (isConfirmed) 
        {
            try {
                await updateAppointmentStatus(appointmentId, title); 
                setStatus([title, appointmentId]); 
            } catch (error) {
                console.error("Failed to update status:", error);
            }
        }
    };

    const updateAppointmentStatus = async (appointmentId, title) => {
        if(status != []){
            if (title == "Confirmed"){
                enqueueSnackbar('Appointment Request Confirmed', { variant: 'success' });
            }
            else {
                enqueueSnackbar('Appointment Request Declined!', { variant: 'error' });
            }
            try {
                const currentAppointment = await fetch(`https://localhost:7169/api/Appointment/${appointmentId}`);
                const appointmentData = await currentAppointment.json();
                
                const updatedData = {
                ...appointmentData, 
                appointmentStatus: title, 
                };

                console.log(updatedData);
                const response = await fetch(`https://localhost:7169/api/Appointment/${appointmentId}`, {
                method: 'PUT', 
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedData),
            });
      
            } catch (error) {
                console.error('Error updating appointment:', error);
            }
        }
    };

    useEffect(() => {
        const page = 1; 
        const pageSize = 7;
        fetch(`https://localhost:7169/api/Appointment?page=${page}&page_size=${pageSize}`)
        .then(response => {
            if (!response.ok) { 
            throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            setItems(data);
            setLoading(false); 
        })
        .catch(error => {
            console.error('Error fetching data:', error);
            setError(error); 
            setLoading(false);
        });
    }, [status]);

    if (loading) {
        return <div>Loading...</div>; 
    }

    if (error) {
        return <div>Error: {error.message}</div>; 
    }

    return (
        <div>
            <div className=" bg-blue-100 h-screen w-screen flex items-center justify-center">
                <div className="bg-gray-200 w-2/6 min-w-[400px] h-5/6 pt-20 pr-20 pl-20 pb-20 ">
                    <div className= "top-20 mb-12">
                        <text className="text-xl font-bold">Appoitment Request</text>
                        
                        <button className="pt-2 pb-2 pr-4 pl-4 ml-[216px]">
                            <span className="flex items-center text-cyan-500 font-medium">
                                View All
                                <img 
                                className="w-8 h-8 ml-2" 
                                src="https://img.icons8.com/ios-glyphs/30/22C3E6/more-than.png" 
                                alt="more than icon" 
                                aria-hidden="true" 
                                />
                            </span>
                        </button>
                    </div>

                    <div className="bg-grey-50 h-[502px] pt-12 pl-12 pr-12 w-full ">
                        <div className="">
                        {items.map((item, index) => (
                            <div className="flex mb-20 ">
                                    <div className="ml-8 w-52 h-52 overflow-hidden flex-none" key={index}>
                                        <img className="w-full h-full object-cover rounded-full" src={item.avatar}></img>
                                    </div>
                                    <div className="ml-16 w-64 flex-auto ">
                                        <p className="mt-4 text-lg font-medium">
                                            {item.fullName}
                                        </p>
                                        <p className="mt-4 text-sm">
                                            {item.gender} {item.age}, {(() => {
                                                const date = new Date(item.appointmentTime);
                                                if (!isNaN(date.getTime())) {
                                                    const day = date.toLocaleString('vi-VN', { day: '2-digit' });
                                                    const month = date.toLocaleString('en-US', { month: 'long' }); 
                                                    const year = date.toLocaleString('vi-VN', { year: 'numeric' });
                                                    const hour = date.toLocaleString('vi-VN', { hour: '2-digit', hour12: false });
                                                    const minute = date.toLocaleString('vi-VN', { minute: '2-digit' });

                                                    let formattedDate= `${day} ${month} ${year} ${hour}:${minute}`;
                                                    
                                                    formattedDate = formattedDate.replace(" giờ", ""); 
                                                    
                                                    return formattedDate;
                                                } else {
                                                    return 'Ngày không hợp lệ'; 
                                                }
                                            })()}
                                        </p>    
                                    </div>
                                    <div className="mt-2 mr-2 flex-auto w-32 font-medium text-sm">
                                        <button 
                                            className={
                                                `mt-8 w-80 pt-6 pb-6 rounded font-medium ml-60 
                                                ${item.appointmentStatus === 'Declined' ? 'bg-red-50 text-red-600':''}
                                                ${item.appointmentStatus === 'Confirmed' ? 'bg-blue-50 text-indigo-600':''}
                                                ${item.appointmentStatus === 'Pending' ? 'hidden':''}
                                                `
                                            }
                                        >{item.appointmentStatus}
                                        </button>
                                        <button
                                            className={
                                                `ml-72 mt-8 flex-auto rounded
                                                ${item.appointmentStatus === 'Declined' ? 'hidden':''}
                                                ${item.appointmentStatus === 'Confirmed' ? 'hidden':''}
                                                ${item.appointmentStatus === 'Pending' ? 'border-red-600 border-2':''}
                                                `
                                            }
                                            onClick={() => handleStatusChange('Declined', item.appointmentId)}
                                        ><img className="h-20 w-20 m-1" src="https://img.icons8.com/sf-regular-filled/50/FA5252/x.png" alt="x"/>
                                        </button>
                                        <button
                                            className={
                                                `flex-auto ml-16 rounded
                                                ${item.appointmentStatus === 'Declined' ? 'hidden':''}
                                                ${item.appointmentStatus === 'Confirmed' ? 'hidden':''}
                                                ${item.appointmentStatus === 'Pending' ? 'border-indigo-600 border-2':''}
                                                `
                                            }
                                            onClick={() => handleStatusChange('Confirmed', item.appointmentId)}
                                        ><img className="h-20 w-20 m-1" src="https://img.icons8.com/ios-filled/50/7950F2/checkmark--v1.png" alt="checkmark--v1"/>
                                        </button>
                                    </div>
                                    <div className="">
                                        
                                    </div>
                            </div>
                            ))}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
  };
  
  export default AppointmentRequest;
