import React from 'react'
import { useState, useEffect } from 'react'
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend
}from 'chart.js';

ChartJS.register(
    ArcElement,
    Tooltip,
    Legend,
);

import { Doughnut } from 'react-chartjs-2';

const GenderChart = () => {

    const [items, setItems] = useState([]);
    const [loading, setLoading] = useState(true); 
    const [error, setError] = useState(null); 
    const [year, setYear] = useState(2024);

    useEffect(() => {
        fetch(`https://localhost:7169/api/Patient/year/${year}`)
        .then(response => {
            if (!response.ok) { 
            throw new Error('Network response was not ok');
            }
            return response.json();
        })
        .then(data => {
            setItems(data);
            setLoading(false); 
        })
        .catch(error => {
            console.error('Error fetching data:', error);
            setError(error); 
            setLoading(false);
        });
    },[year]);

    if (loading) {
        return <div>Loading...</div>; 
    }

    if (error) {
        return <div>Error: {error.message}</div>; 
    }


    const chartData = {
        labels: ['Male', 'Female', 'Child'],
        datasets: [
            {
                label: 'Số lượng bệnh nhân',
                data: [
                    3,6,8
                ],
                backgroundColor: [
                    '#EEBB00',
                    '#8811DD',
                    '#BBDDDD' 
                ],
                hoverBackgroundColor: [
                    '#FF0000',
                    '#0000FF',
                    '#FFFFFF'
                ]
            }
        ]
    };

    const options = {
        plugins: {
            legend: {
                position: 'top',
                labels: {
                    font: {
                        size: 14
                    },
                    color: '#333' 
                }
            },
            tooltip: {
                backgroundColor: '#000',
                bodyColor: '#fff',
                titleFont: {
                    size: 16
                }
            }
        },
        cutout: '60%' 
    };

    return (
        <div>
            <div className="flex items-center justify-center">
                <div className="w-[346px]">
                    <div className= "top-20 mb-16">
                        <text className="text-3xl font-semibold">
                            Gender
                        </text>
                        
                        <select 
                            className="text-gray-800 text-lg pt-4 pb-4 pl-16 pr-12 rounded ml-[158px]" 
                            id="year" 
                            value={year} 
                            onChange={()=> setYear(event.target.value)}
                            >
                            {[2021, 2022, 2023, 2024].map((year) => (
                                <option key={year} value={year}>
                                    {year}
                                </option>
                            ))}
                        </select>
                    </div>
                    <div className="bg-white h-[320px] pl-28 pt-20">
                            <Doughnut
                                className="w-[20%]"
                                data = {chartData}
                                options={options}
                            />
                    </div>
                </div>
                
            </div>
        </div>
    )
}

export default GenderChart;
